export const USER_HOOK = 'USER_HOOK'
export const REQUEST_HOOK = 'REQUEST_HOOK'
export const FRIEND_HOOK = 'FRIEND_HOOK'
export const BLOCKED_HOOK = 'BLOCKED_HOOK'

export const initHookState = {
    userHook: {status: false, id: null},
    requestHook: {status: false, id: null},
    friendHook: {status: false, id: null},
    blockedHook: {status: false, id: null}
}

export const hookReducer = (state, { type, payload }) => {
    switch(type) {
        case USER_HOOK:
            return { ...state, userHook: payload }

        case REQUEST_HOOK:
            return { ...state, requestHook: payload }

        case FRIEND_HOOK:
            return { ...state, friendHook: payload }

        case BLOCKED_HOOK:
            return { ...state, blockedHook: payload }

        default:
            return state
    }
}
