export const socketEmit = (event, socket, data) => socket.emit(event, data)

export const socketOn = (event, socket, object, cb) => {
    socket.on(event, data => {
        if (event === 'fetch-chat') {
            console.log(event)
            console.log(object)
        }
        return cb(data, object)
    })
}
