import React, { useState, useEffect } from "react";
import styled from "styled-components";
import MessageForm from "../forms/MessageForm";
import MessageList from "../messages/MessageList";
import ChatHeader from "../header/ChatHeader";
import Dropdown from "../dropdown/Dropdown";
import DropdownItem from "../dropdown/DropdownItem";
import { useAppHooks } from "../../contexts";
import devices from "../../utils/devices";
import api from "../../api";

const ChatStyle = styled.div`
  width: auto;
  position: relative;
  overflow: hidden;
  max-height: 100vh;
  overflow: hidden;

  @media ${devices.mobileL} {
    width: 100vw;
  }
`;

const Chat = ({ id }) => {
  const { useTransition, socket } = useAppHooks();

  const [{ chatSelected }, _] = useTransition;

  const [chat, setChat] = useState(null);
  const [y, setY] = useState(0);
  const [isDisplayed, setDisplay] = useState(false);
  const [loading, setLoading] = useState(true);

  const getHeaderPosition = (isDisplayed, y) => {
    setDisplay(isDisplayed);
    setY(y);
  };

  useEffect(() => {
    // if (!chat && !chatSelected) {
    //   socket.on("fetch-chat", chatFetched => {
    //     setChat(chatFetched);
    //   });
    // }
    const fetchChat = async () => {
      try {
        const res = await api.chat.getChat(id)
        setChat(res.data)
        setLoading(false)
      } catch (error) {
        console.log(error)
        setLoading(false)
      }
    }

    fetchChat()
  }, [chat, id]);

  console.log(location)
  console.log(match)
 
  return (
    <ChatStyle>
      {
        loading && <h3>Loading...</h3>
      }
      {!loading && chat && (
        <div>
          <ChatHeader
            getHeaderPosition={getHeaderPosition}
            isDisplayed={isDisplayed}
            chat={chat}
          />
          <Dropdown
            propsY={y}
            isDisplayed={isDisplayed}
            handleDropdown={setDisplay}
          >
            <DropdownItem
              handleClick={() => setDisplay(false)}
              text="Add User to Chat"
            />
          </Dropdown>
          <MessageList messages={chat.messages} users={chat.users} />
          <MessageForm chatId={chat.id} />
        </div>
      )}
    </ChatStyle>
  );
};

export default Chat;
