import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { useAppHooks } from "../../contexts";
import api from "../../api";
import Request from "./Request";

const RequestsContainer = styled.div`
  padding: 0;
  margin: 0;
  width: 100%;

  & h4 {
    margin-left: 15px;
  }
`;

const RequestList = styled.ul`
  list-style: none;
  margin: 0;
  padding: 0;
  width: 100%;
  color: black;

  & > .request-label {
    width: 100%;
    height: 100%;
    padding: 12px 8px;
    text-align: right;
    background-image: linear-gradient(0deg, rgba(178, 178, 178, 0.1) 0%, rgba(178, 178, 178, 0.1) 76%,rgba(46, 46, 46, 0.1) 76%, rgba(46, 46, 46, 0.1) 100%),linear-gradient(45deg, rgba(199, 199, 199, 0.04) 0%, rgba(199, 199, 199, 0.04) 24%,rgba(11, 11, 11, 0.04) 24%, rgba(11, 11, 11, 0.04) 100%),linear-gradient(135deg, rgba(82, 82, 82, 0.09) 0%, rgba(82, 82, 82, 0.09) 57%,rgba(108, 108, 108, 0.09) 57%, rgba(108, 108, 108, 0.09) 100%),linear-gradient(0deg, rgba(243, 243, 243, 0.09) 0%, rgba(243, 243, 243, 0.09) 23%,rgba(31, 31, 31, 0.09) 23%, rgba(31, 31, 31, 0.09) 100%),linear-gradient(45deg, rgba(153, 153, 153, 0.06) 0%, rgba(153, 153, 153, 0.06) 70%,rgba(233, 233, 233, 0.06) 70%, rgba(233, 233, 233, 0.06) 100%),linear-gradient(90deg, rgb(62, 230, 74),rgb(4, 224, 249));
    background-color: #192a56;
  }

  &::-webkit-scrollbar {
    width: 8px;
    background: #2c3e50;
  }

  &::-webkit-scrollbar-thumb {
    background-color: #243140;
  }
`;

const Requests = () => {
    const { useAuth } = useAppHooks();
    const [{ user }, _] = useAuth;

    const [requests, setRequests] = useState([]);
    const [loading, setLoading] = useState(true)

    useEffect(() => {
      const getRequests = async () => {
        if (user) {
            let res = await api.user.getRequestList(user.id)
            if (res.data.length > 0) setRequests(res.data)
        }
        setLoading(false)
      }

      getRequests()
    }, [])

    console.log(requests)

    return (
      <RequestsContainer>
          <RequestList>
            <div className='request-label'>Friends request: <span>{requests.length > 0 ? requests.length : 0}</span></div>
            {!loading && requests.length === 0 && <h4>You have no requests. Start search amazing people.</h4>}
            {!loading && requests.length > 0 && requests.map(request => <Request key={request.id} request={request} />)}
          </RequestList>
      </RequestsContainer>
    )
};

export default Requests;
