import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { useAppHooks } from "../../contexts";
import api from "../../api";
import AcceptIcon from "../icons/AcceptIcon";

const RequestStyle = styled.li`
  margin: 0;
  padding: 4px 0;
  cursor: pointer;
  width: auto;
  height: 40px;
  display: flex;
  align-items: center;
  color: white;
  transition: all 300ms ease-in;

  &:hover {
    background-color: #32465a;
    padding-left: 16px;
    border-right: 0.6em solid white;
  }

  & .request-name {
    font-size: 1.5em;
    letter-spacing: 4px;
  }

  & .request-actions {
    margin-left: auto;
    margin-right: 8px;
  }
`;

const Request = ({ request }) => {
    const { useAuth  } = useAppHooks();
    const [{ user }, dispatchAuth] = useAuth;

    const [contact, setContact] = useState(null)

    const acceptFriendRequest = async () => {
        // socket.emit("new-chat", Requests);
        try {
            let res = await api.user.addFriend(request.id, user.id);
            if (res.data) {
                alert(res.data.msg)
            }
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        const fetchUser = async () => {
            try {
                let res = await api.user.getUser(request.requesterId)
                if (res.data) {
                    setContact(res.data)
                }
            } catch (error) {
                console.log(error)    
            }
        }

        fetchUser()
    }, [])

    return (
        <RequestStyle>
            <span className="request-name">{contact && contact.name}</span>
            <span className="request-actions">
                <AcceptIcon handleClick={acceptFriendRequest} />
            </span>
        </RequestStyle>
    );
};

export default Request;
